mapper.select:
select name
from donationPurpose
where ID=#{ID}

select c2.name 
from [DonationPurpose] d join [Corporate] c2 on d.corporateID = c2.ID
where d.ID=#{ID}

mapper.update:
UPDATE [DonationPurpose]
SET corporateID =
#{corporateID}, organizationID = #{organizationID},
name = #{name},
gubun = #{gubun}
WHERE ID = #{ID}
