mapper.select:
select c2.*
from CodeGroup c1 left join Code c2 on c1.ID=c2.codeGroupID
where c1.name=#{name}

select c2.*
from CodeGroup c1 left join Code c2 on c1.ID=c2.codeGroupID
where c1.name=#{bank}

select c2.*
from CodeGroup c1 left join Code c2 on c1.ID=c2.codeGroupID
where c1.name=#{name}

select c2.*
from CodeGroup c1 left join Code c2 on c1.ID=c2.codeGroupID
where c1.name=#{name1} or c1.name=#{name2}

select c2.*
from CodeGroup c1 left join Code c2 on c1.ID=c2.codeGroupID
where c1.name=#{name}

select codeName
from code
where ID=#{ID}
